import React, { useState, useEffect } from 'react';
import './App.css';
import './VideoGallery.js';

function App() {
  const [apodData, setApodData] = useState(null);
  const API_KEY = 'qvTugfpNj9qF2lpjhjW33mcjZclHYKpfRLLHCZgL';

  useEffect(() => {
    fetch(`https://api.nasa.gov/planetary/apod?api_key=${API_KEY}`)
      .then(response => response.json())
      .then(data => setApodData(data))
      .catch(error => console.error("Error al obtener los datos:", error));
  }, []);

  if (!apodData) return <div>Cargando...</div>;

  return (
    <div className="app">
      <header className="header">
        <h1>CosmoVisor</h1>
        <nav>
          <ul>
            <li>Inicio</li>
            <li>Explora Videos</li>
            <li>Galería</li>
            <li>Acerca de</li>
          </ul>
        </nav>
      </header>

      {/* Sección Hero con la imagen de APOD */}
      <section className="hero">
        <div className="hero-bg">
          <img src={apodData.url} alt={apodData.title} />
        </div>
        <div className="hero-content">
          <h2>{apodData.title}</h2>
          <p>{apodData.explanation}</p>
          <button>Explora Ahora</button>
        </div>
      </section>

      {/* Sección para la galería de videos */}
      <section className="videos-section">
        <VideoGallery />
      </section>
    </div>
  );
}

export default App;
