import React, { useState, useEffect } from 'react';
import './App.css';
import VideoGallery from './VideoGallery';

function App() {
  const [apodData, setApodData] = useState(null);
  const API_KEY = 'TU_API_KEY_PERSONAL'; // Reemplaza con tu API key

  useEffect(() => {
    fetch(`https://api.nasa.gov/planetary/apod?api_key=${API_KEY}`)
      .then(response => response.json())
      .then(data => setApodData(data))
      .catch(error => console.error("Error al obtener los datos:", error));
  }, []);

  if (!apodData) return <div>Cargando...</div>;

  return (
    <div className="app" style={{
        backgroundImage: `url(${apodData.url})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}>
      <header className="header">
        <h1>CosmoVisor</h1>
        <nav>
          <ul>
            <li>Inicio</li>
            <li>Explora Videos</li>
            <li>Galería</li>
            <li>Acerca de</li>
          </ul>
        </nav>
      </header>
      <main className="hero">
        <div className="hero-content">
          <h2>{apodData.title}</h2>
          <p>{apodData.explanation}</p>
          <button>Explora Ahora</button>
        </div>
      </main>

      {/* Sección para la galería de videos */}
      <section className="videos-section">
        <VideoGallery />
      </section>
    </div>
  );
}

export default App;
