import React, { useState, useEffect } from 'react';
import './VideoGallery.css';

function VideoGallery() {
  const [videos, setVideos] = useState([]);
  const [selectedVideo, setSelectedVideo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Realizamos una búsqueda filtrando "misiones espaciales 4k"
    fetch('https://images-api.nasa.gov/search?q=misiones+espaciales+4k&media_type=video')
      .then(response => response.json())
      .then(data => {
        console.log(data); // Para inspeccionar la estructura de los datos
        if (data.collection && data.collection.items) {
          const items = data.collection.items;
          // Filtrar videos que, además, en el título o descripción incluyan "4k"
          const validVideos = items.filter(video => {
            const title = video.data[0].title.toLowerCase();
            const description = video.data[0].description ? video.data[0].description.toLowerCase() : '';
            return title.includes('4k') || description.includes('4k');
          });
          setVideos(validVideos);
          if (validVideos.length > 0) {
            setSelectedVideo(validVideos[0]);
          }
        }
        setLoading(false);
      })
      .catch(error => {
        console.error("Error al obtener los videos:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Cargando videos...</div>;
  }

  // Si no hay videos válidos, se muestra un mensaje
  if (videos.length === 0) {
    return <div>No se encontraron videos en 4K reproducibles.</div>;
  }

  // Datos del video seleccionado
  const selectedData = selectedVideo?.data[0];
  const selectedVideoLink = selectedVideo?.links && selectedVideo.links[0] ? selectedVideo.links[0].href : '';

  return (
    <div className="video-gallery">
      <h2>Videos de Misiones Espaciales en 4K</h2>
      <div className="video-display">
        <div className="video-selected">
          {selectedVideoLink ? (
            <video controls width="100%">
              <source src={selectedVideoLink} type="video/mp4" />
              Tu navegador no soporta el elemento video.
            </video>
          ) : (
            <p>No se encontró el video seleccionado.</p>
          )}
          {selectedData && <h3>{selectedData.title}</h3>}
        </div>
      </div>
      <div className="video-slider">
        {videos.map((video, index) => {
          const data = video.data[0];
          const videoLink = video.links && video.links[0] ? video.links[0].href : '';
          return (
            <div
              key={index}
              className={`slider-item ${selectedVideo === video ? 'active' : ''}`}
              onClick={() => setSelectedVideo(video)}
            >
              {videoLink ? (
                <video muted>
                  <source src={videoLink} type="video/mp4" />
                </video>
              ) : (
                <p>No video</p>
              )}
              <p>{data.title}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default VideoGallery;
