import React, { useState, useEffect } from 'react';

function VideoGallery() {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Se realiza una búsqueda de videos relacionados con "space"
    fetch('https://images-api.nasa.gov/search?q=space&media_type=video')
      .then(response => response.json())
      .then(data => {
        if (data.collection && data.collection.items) {
          setVideos(data.collection.items);
        }
        setLoading(false);
      })
      .catch(error => {
        console.error("Error al obtener los videos:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Cargando videos...</div>;
  }

  return (
    <div className="video-gallery">
      <h2>Galería de Videos</h2>
      <div className="videos-container">
        {videos.map((video, index) => {
          // Cada objeto video contiene un array "data" con información y "links" con el video.
          const videoData = video.data[0];
          // Intentamos obtener la URL del primer enlace
          const videoLink = video.links && video.links[0] ? video.links[0].href : '';

          return (
            <div key={index} className="video-item">
              <h3>{videoData.title}</h3>
              {videoLink ? (
                <video controls width="400">
                  <source src={videoLink} type="video/mp4" />
                  Tu navegador no soporta video HTML5.
                </video>
              ) : (
                <p>No se encontró el video.</p>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default VideoGallery;
